import "./Description.css";
import { useAuthUser, useIsAuthenticated } from "react-auth-kit";

function LandingPage() {
  const auth = useAuthUser();
  const isAuthenticated = useIsAuthenticated();

  return (
    <div className="landing-page">
      {isAuthenticated() && (
        <div className="greeting-container">
          <p className="greeting-text">
            Welcome back, <span className="user-name">{auth().firstName}</span>!
          </p>
        </div>
      )}
      <header>
        <div className="logo-container">
          <h1 className="title">TechLecture</h1>
          <img
            className="logo"
            src="https://i.pinimg.com/736x/2a/82/fe/2a82fe343e74c262f7638fe38520d926.jpg"
            alt="TechLecture Logo"
          />
        </div>
        <p className="description">
          Connecting you to the latest in technology and innovation.
        </p>
        <p className="product-description">
          Discover a world of endless possibilities with TechLecture. Our
          platform is designed to keep you connected to the forefront of
          technology and innovation. From in-depth articles to engaging videos,
          TechLecture provides you with a rich learning experience that empowers
          you to stay ahead in the ever-evolving tech landscape. Whether you're
          an enthusiast, a professional, or just curious, TechLecture is your
          gateway to explore, learn, and thrive in the world of technology.
        </p>
      </header>
    </div>
  );
}

export default LandingPage;
