import React, { useEffect, useState } from "react";
import "./NewFilePopUp.css";

const NewFilePopUp = ({ userFullName, userFileUrl }) => {
  const [showFlash, setShowFlash] = useState(false);

  useEffect(() => {
    // כאשר משנים את ה-props, נפעיל את האנימציה
    setShowFlash(true);

    // לאחר שהאנימציה הסתיימה, נכבה את האנימציה
    const timeout = setTimeout(() => {
      setShowFlash(false);
    }, 2000); // זמן האנימציה

    // לניקוי timeout בכל פעם שהקומפוננטה מתאפשרת
    return () => clearTimeout(timeout);
  }, [userFullName, userFileUrl]); // הפעלת האפקט בשינוי של userFullName או userFileUrl

  return (
    <div className={`window-container ${showFlash ? "flash" : ""}`}>
      <div className="image-preview">
        <div className="overlay">
          <p>
            {`Your student: ${userFullName} uploaded a new file.`}
            <br />
            <a target="_blank" href={userFileUrl}>
              Open It
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default NewFilePopUp;
