import { useEffect, useState } from "react";
import "./LectureList.css";
import { useAuthUser } from "react-auth-kit";
import { Navigate } from "react-router-dom";

const LectureList = () => {
  const auth = useAuthUser();
  const userEmail = auth().email;
  const [userActivePresentations, setUserActivePresentations] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const getUserActivePresentations = async () => {
      const response = await fetch(
        "https://techlectureback.onrender.com/userActivePresentations/" +
          userEmail
      );
      const data = await response.json();
      if (data.status === 200) {
        setUserActivePresentations(data.presentations);
      }
      setLoading(false);
    };
    getUserActivePresentations();
  }, [userEmail]);

  const handleDeletePresentation = async (presentationID) => {
    const response = await fetch(
      "https://techlectureback.onrender.com/deleteUserActivePresentation/",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ presentationID: presentationID }),
      }
    );
    const data = await response.json();
    if (data.status === 200) {
      setUserActivePresentations(
        userActivePresentations.filter(
          (presentation) => presentation._id !== presentationID
        )
      );
    } else {
      console.log(data.message);
    }
  };

  return (
    <div className="lecture-list-container">
      <h2 className="lecture-list-title">Your Active Lectures List</h2>
      {loading ? (
        <p>Loading...</p>
      ) : userActivePresentations.length === 0 ? (
        <p className="no-presentations-message">
          You have no active presentations.
        </p>
      ) : (
        <ul className="lecture-list">
          {userActivePresentations.map((presentation, index) => (
            <li key={index} className="lecture-item">
              <div className="lecture-info">
                <p className="serial-number">Lecture Number: {index + 1}</p>
                <p className="lecture-name">
                  Topic: {presentation.lectureTopic}
                </p>
                <p className="lecture-date">Date: {presentation.date}</p>
              </div>
              <div className="lecture-actions">
                <a
                  href={`http://localhost:3000/lectures/lecturerPosition/${presentation.fileNumber}`}
                  className="lecture-link"
                >
                  Link To Lecture
                </a>
                <button
                  onClick={() => handleDeletePresentation(presentation._id)}
                  className="delete-button"
                >
                  Delete Lecture
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default LectureList;
