import React, { useState } from "react";
import "./NewPresenationForm.css";
import QRCodeGenerator from "../../../components/QR/QRCodeGenerator";
import { useAuthUser } from "react-auth-kit";
import { Link } from "react-router-dom";

function NewPresenationForm() {
  const auth = useAuthUser();
  const lecturerFullName = auth().firstName + " " + auth().lastName;
  const [lectureTopic, setLectureTopic] = useState("");
  const [lectureHours, setLectureHours] = useState("");
  const [lectureFile, setLectureFile] = useState(null);
  const [fileUploadSuccessfully, setFileUploadSuccessfully] = useState(false);
  const [lecturerPresenationAddress, setLecturerPresenationAddress] =
    useState("");
  const [studentPresentationAddress, setStudentPresentationAddress] =
    useState("");
  const [fileName, setFileName] = useState("");

  if (fileUploadSuccessfully) {
    // return <Navigate to={presenationAddress} replace={true} />;
  }

  const handleTopicChange = (event) => {
    setLectureTopic(event.target.value);
  };

  const handleHoursChange = (event) => {
    setLectureHours(event.target.value);
  };

  const handleFileChange = (event) => {
    setLectureFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("topic", lectureTopic);
    formData.append("hours", lectureHours);
    formData.append("file", lectureFile);
    formData.append("lecturerName", lecturerFullName);
    formData.append("lecturerEmail", auth().email);
    formData.append("date", new Date().toUTCString().slice(5, 16));

    try {
      const response = await fetch(
        "https://techlectureback.onrender.com/upload",
        {
          method: "POST",
          body: formData,
        }
      );

      if (response.status === 200) {
        const data = await response.json();
        console.log(data);
        setFileUploadSuccessfully(true);
        setLecturerPresenationAddress(
          `/lectures/lecturerPosition/${data.fileName}/`
        );
        setStudentPresentationAddress(
          `/lectures/studentPosition/${data.fileName}/`
        );
        setFileName(data.fileName);
      } else {
        console.log("Upload failed");
      }
    } catch (error) {
      console.log(error);
    }

    setLectureTopic("");
    setLectureHours("");
    setLectureFile(null);
  };

  return (
    <div className="lecture-form">
      <img
        src={
          "https://i.pinimg.com/736x/2a/82/fe/2a82fe343e74c262f7638fe38520d926.jpg"
        }
        alt="Logo"
        className="presentationFormLogo"
      />
      <h2>Start the Lecture</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <label htmlFor="lectureTopic">Lecture Topic:</label>
        <br />
        <input
          type="text"
          id="lectureTopic"
          name="lectureTopic"
          value={lectureTopic}
          onChange={handleTopicChange}
          required
        />
        <br />
        <br />
        <label htmlFor="lectureHours">
          Lecture Hours (e.g., 09:00 - 10:30):
        </label>
        <br />
        <input
          type="text"
          id="lectureHours"
          name="lectureHours"
          value={lectureHours}
          onChange={handleHoursChange}
          required
        />
        <br />
        <br />
        <label htmlFor="lectureFile">Upload Lecture File:</label>
        <br />
        <input
          type="file"
          id="lectureFile"
          name="lectureFile"
          accept=".pdf,.ppt,.pptx,.jpg,.jpeg,.png"
          onChange={handleFileChange}
          required
        />
        <br />
        <br />
        <button type="submit">Submit Lecture</button>

        {fileUploadSuccessfully && (
          <QRCodeGenerator
            url={`https://techlecturefront.onrender.com/${studentPresentationAddress}`}
          />
        )}

        {fileUploadSuccessfully && <h3>{"Presentation Code: " + fileName}</h3>}

        {fileUploadSuccessfully && (
          <Link
            to={`https://techlecturefront.onrender.com/${lecturerPresenationAddress}`}
          >
            Move to Lecturer Position
          </Link>
        )}
        <br />
        <br />

        {fileUploadSuccessfully && (
          <Link
            to={`https://techlecturefront.onrender.com/${studentPresentationAddress}`}
          >
            Move to Student Position
          </Link>
        )}
      </form>

      <div className="technology-icons">
        <img
          src={
            "https://png.pngtree.com/png-clipart/20191122/original/pngtree-computer-icon-png-image_5172918.jpg"
          }
          alt="Technology Icon 1"
        />
        <img
          src={
            "https://previews.123rf.com/images/sn333g/sn333g1604/sn333g160400063/55793540-presentation-linear-icon-vector-business-presentation-symbol-or-logo-element-in-thin-line-style.jpg"
          }
          alt="Technology Icon 2"
        />
        <img
          src={
            "https://w7.pngwing.com/pngs/569/605/png-transparent-internet-icon-internet-access-web-browser-circle-with-line-through-it-angle-text-logo.png"
          }
          alt="Technology Icon 3"
        />
      </div>
    </div>
  );
}

export default NewPresenationForm;
